import { Component, OnInit } from '@angular/core';
import * as Highcharts from 'highcharts';
import OrganizationModule from 'highcharts/modules/organization';
import Exporting from 'highcharts/modules/exporting';

OrganizationModule(Highcharts);
Exporting(Highcharts);

interface Field {
  id: string;
}
interface Database {
  id: string;
  fields: Field[];
}
interface System {
  id: string;
  databases: Database[];
}

@Component({
  selector: 'app-horizontal-organization-chart-visualizer',
  templateUrl: './horizontal-organization-chart-visualizer.component.html',
  styleUrls: ['./horizontal-organization-chart-visualizer.component.css']
})

export class HorizontalOrganizationChartVisualizerComponent {
   data: { systems: System[] } = {
    systems: [
      {
        id: 'System A',
        databases: [
          { id: 'DB1', fields: [{ id: 'Field1' }, { id: 'Field2' }] },
          { id: 'DB2', fields: [{ id: 'Field3' }] }
        ]
      },
      {
        id: 'System B',
        databases: [
          { id: 'DB3', fields: [{ id: 'FieldA' }] },
          { id: 'DB4', fields: [{ id: 'FieldB' }] }
        ]
      }
    ]
  };

  selectedSystemId: string | null = null;
  selectedDBId: string | null = null;
  newFieldId: string = '';

  Highcharts: typeof Highcharts = Highcharts;
  chartOptions: Highcharts.Options = {};

  ngOnInit(): void {
    if (this.data.systems.length > 0) {
      this.selectedSystemId = this.data.systems[0].id;
      const firstSystem = this.getSelectedSystem();
      if (firstSystem && firstSystem.databases.length > 0) {
        this.selectedDBId = firstSystem.databases[0].id;
      }
    }
    this.updateChart();
  }

  getSelectedSystem(): System | undefined {
    return this.data.systems.find(s => s.id === this.selectedSystemId);
  }

  getDatabasesForSelectedSystem(): Database[] {
    return this.getSelectedSystem()?.databases || [];
  }

  addField(): void {
    if (!this.newFieldId || !this.selectedSystemId || !this.selectedDBId) {
      alert('Please select system, database and enter field name');
      return;
    }

    const system = this.getSelectedSystem();
    if (!system) return;

    const db = system.databases.find(d => d.id === this.selectedDBId);
    if (!db) return;

    if (db.fields.find(f => f.id === this.newFieldId)) {
      alert('Field already exists');
      return;
    }

    db.fields.push({ id: this.newFieldId });
    this.newFieldId = '';
    this.updateChart();
  }

  updateChart() {
    const chartNodes: any[] = [];
    const chartLinks: any[] = [];

    this.data.systems.forEach(system => {
      chartNodes.push({ id: system.id, title: system.id, name: 'System' });

      system.databases.forEach(db => {
        chartNodes.push({ id: db.id, title: db.id, name: 'Database' });
        chartLinks.push([system.id, db.id]);

        db.fields.forEach(field => {
          chartNodes.push({ id: field.id, title: field.id, name: 'Field' });
          chartLinks.push([db.id, field.id]);
        });
      });
    });

    this.chartOptions = {
      chart: {
        height: 600,
        inverted: true
      },
      title: {
        text: 'Organization Chart: Systems → Databases → Fields'
      },
      series: [{
        type: 'organization',
        name: 'Systems Structure',
        keys: ['from', 'to'],
        data: chartLinks,
        nodes: chartNodes,
        colorByPoint: false,
        color: '#007ad0',
        borderColor: 'white',
        nodeWidth: 100
      }],
      tooltip: {
        outside: true
      },
      exporting: {
        allowHTML: true,
        sourceWidth: 800,
        sourceHeight: 600
      }
    };
  }
}
