import { Component, OnInit } from '@angular/core';
import * as Highcharts from 'highcharts';

interface Field {
  id: string;
}

interface Database {
  id: string;
  fields: Field[];
}

interface System {
  id: string;
  databases: Database[];
}

@Component({
  selector: 'app-flow-visualizer',
  templateUrl: './flow-visualizer.component.html',
  styleUrls: ['./flow-visualizer.component.css']
})

export class FlowVisualizerComponent implements OnInit {
    Highcharts: typeof Highcharts = Highcharts;

  chartOptions: Highcharts.Options = {
    chart: {
      type: 'organization',
      inverted: true,
      height: 600
    },
    title: {
      text: 'System Flow'
    },
    series: [{
      type: 'organization',
      name: 'Org Chart',
      keys: ['from', 'to'],
      data: [
        ['System A', 'DB1'],
        ['System A', 'DB2'],
        ['DB1', 'Field1'],
        ['DB1', 'Field2'],
        ['DB2', 'Field3'],
        ['System B', 'DB3'],
        ['System B', 'DB4'],
        ['DB3', 'FieldA'],
        ['DB4', 'FieldB']
      ],
      nodes: [
        { id: 'System A', title: 'System A', name: 'System A' },
        { id: 'DB1', title: 'DB1', name: 'Database 1' },
        { id: 'DB2', title: 'DB2', name: 'Database 2' },
        { id: 'Field1', title: 'Field1', name: 'Field 1' },
        { id: 'Field2', title: 'Field2', name: 'Field 2' },
        { id: 'Field3', title: 'Field3', name: 'Field 3' },
        { id: 'System B', title: 'System B', name: 'System B' },
        { id: 'DB3', title: 'DB3', name: 'Database 3' },
        { id: 'DB4', title: 'DB4', name: 'Database 4' },
        { id: 'FieldA', title: 'FieldA', name: 'Field A' },
        { id: 'FieldB', title: 'FieldB', name: 'Field B' }
      ],
      colorByPoint: false,
      color: '#007ad0',
      dataLabels: {
        color: 'white'
      },
      borderColor: 'white',
      nodeWidth: 65
    }],
    tooltip: {
      outside: true
    },
    exporting: {
      allowHTML: true
    }
  };

  ngOnInit(): void {}
}
<div class="flow-visualizer-container">
  <label>
    Select System:
    <select [(ngModel)]="selectedSystemId" (change)="selectedDBId = null">
      <option *ngFor="let sys of data.systems" [value]="sys.id">{{ sys.id }}</option>
    </select>
  </label>

  <label>
    Select Database:
    <select [(ngModel)]="selectedDBId" [disabled]="!selectedSystemId">
      <option *ngFor="let db of getDatabasesForSelectedSystem()" [value]="db.id">{{ db.id }}</option>
    </select>
  </label>

  <label>
    New Field Name:
    <input type="text" [(ngModel)]="newFieldId" placeholder="Enter field name" />
  </label>

  <button (click)="addField()">Add Field</button>
</div>

<hr />

<highcharts-chart
  [Highcharts]="Highcharts"
  [options]="chartOptions"
  style="width: 100%; height: 400px; display: block;"
>
</highcharts-chart>
