import { Component, AfterViewInit } from '@angular/core';
import * as Highcharts from 'highcharts';
import OrganizationModule from 'highcharts/modules/organization';
import ExportingModule from 'highcharts/modules/exporting';
import ExportData from 'highcharts/modules/export-data';

// Initialize modules
OrganizationModule(Highcharts);
ExportingModule(Highcharts);
ExportData(Highcharts);

@Component({
  selector: 'app-horizontal-organization-chart-visualizer',
  templateUrl: './horizontal-organization-chart-visualizer.component.html',
  styleUrls: ['./horizontal-organization-chart-visualizer.component.css']
})
export class HorizontalOrganizationChartVisualizerComponent implements AfterViewInit {
  ngAfterViewInit(): void {
    Highcharts.chart('container', {
      chart: {
        height: 600,
        inverted: false
      },
      title: {
        text: 'Carnivora Phylogeny'
      },
      subtitle: {
        text: 'Tracing the Evolutionary Relationship of Carnivores'
      },
      plotOptions: {
        series: {
          nodeWidth: '22%'
        }
      },
      accessibility: {
        point: {
          descriptionFormat: '{toNode.name} ' +
            '{#if (eq toNode.level 1 )} is a distinct family within the ' +
            'order of {fromNode.id}. {toNode.custom.info}{/if}' +
            '{#if (eq toNode.level 2 )} is a genus within the {fromNode.id}. ' +
            '{toNode.custom.info} {/if}' +
            '{#if (eq toNode.level 3 )} is a species within the ' +
            '{fromNode.id}. {toNode.custom.info} {/if}'
        }
      },
      series: [{
        type: 'organization',
        name: 'Carnivora Phylogeny',
        keys: ['from', 'to'],
        data: [
          ['Carnivora', 'Felidae'],
          ['Carnivora', 'Mustelidae'],
          ['Carnivora', 'Canidae'],
          ['Felidae', 'Panthera'],
          ['Mustelidae', 'Taxidea'],
          ['Mustelidae', 'Lutra'],
          ['Panthera', 'Panthera pardus'],
          ['Taxidea', 'Taxidea taxus'],
          ['Lutra', 'Lutra lutra'],
          ['Canidae', 'Canis'],
          ['Canis', 'Canis latrans'],
          ['Canis', 'Canis lupus']
        ],
        levels: [
          { level: 0, color: '#DEDDCF', dataLabels: { color: 'black' } },
          { level: 1, color: '#DEDDCF', dataLabels: { color: 'black' }, height: 25 },
          { level: 2, color: '#DEDDCF', dataLabels: { color: 'black' } },
          { level: 3, dataLabels: { color: 'black' } }
        ],
        nodes: [
          {
            id: 'Carnivora',
            name: 'Carnivora',
            custom: {
              info: 'Carnivora is a diverse scrotiferan order...'
            }
          },
          {
            id: 'Felidae',
            name: 'Felidae',
            color: '#fcc657',
            custom: {
              info: 'Felidae is a family of mammals in the order Carnivora...'
            }
          },
          {
            id: 'Panthera',
            name: 'Panthera',
            color: '#fcc657',
            custom: { info: 'Panthera' }
          },
          {
            id: 'Panthera pardus',
            name: 'Panthera pardus',
            color: '#fcc657',
            image: 'https://www.highcharts.com/samples/graphics/horizontal-organizational-chart/panthera.png',
            custom: {
              info: 'Panthera is a genus within the Felidae family...'
            }
          },
          {
            id: 'Mustelidae',
            name: 'Mustelidae',
            color: '#C4B1A0',
            custom: { info: 'The Mustelidae are a family of carnivorous mammals...' }
          },
          {
            id: 'Taxidea',
            name: 'Taxidea',
            color: '#C4B1A0',
            custom: { info: 'Taxidea' }
          },
          {
            id: 'Lutra',
            name: 'Lutra',
            color: '#C4B1A0',
            custom: { info: 'Lutra' }
          },
          {
            id: 'Taxidea taxus',
            name: 'Taxidea taxus',
            color: '#C4B1A0',
            image: 'https://www.highcharts.com/samples/graphics/horizontal-organizational-chart/taxidea-taxus.png',
            custom: {
              info: 'Taxidea taxus is a North American badger...'
            }
          },
          {
            id: 'Lutra lutra',
            name: 'Lutra lutra',
            color: '#C4B1A0',
            image: 'https://www.highcharts.com/samples/graphics/horizontal-organizational-chart/lutra.png',
            custom: {
              info: 'Lutra is a semiaquatic mammal native to Eurasia...'
            }
          },
          {
            id: 'Canidae',
            name: 'Canidae',
            color: '#B0ACA2',
            custom: {
              info: 'The biological family Canidae includes domestic dogs, wolves...'
            }
          },
          {
            id: 'Canis',
            name: 'Canis',
            color: '#B0ACA2',
            custom: { info: 'Canis' }
          },
          {
            id: 'Canis latrans',
            name: 'Canis latrans',
            color: '#B0ACA2',
            image: 'https://www.highcharts.com/samples/graphics/horizontal-organizational-chart/canis-latrans.png',
            custom: {
              info: 'Canis latrans, is a canine native to North America...'
            }
          },
          {
            id: 'Canis lupus',
            name: 'Canis lupus',
            color: '#B0ACA2',
            image: 'https://www.highcharts.com/samples/graphics/horizontal-organizational-chart/canis-lupus.png',
            custom: {
              info: 'Canis lupus is a canine native to the wilderness...'
            }
          }
        ],
        colorByPoint: false,
        borderColor: 'black',
        borderWidth: 2
      }],
      tooltip: {
        outside: true,
        format: '{point.custom.info}',
        style: {
          width: '320px'
        }
      },
      exporting: {
        allowHTML: true,
        sourceWidth: 800,
        sourceHeight: 600
      }
    });
  }
}
