import { Component } from '@angular/core';
import { Component, OnInit } from '@angular/core';
import * as Highcharts from 'highcharts';
import OrganizationModule from 'highcharts/modules/organization';
import Exporting from 'highcharts/modules/exporting';

OrganizationModule(Highcharts);
Exporting(Highcharts);

interface Field {
  id: string;
}
interface Database {
  id: string;
  fields: Field[];
}
interface System {
  id: string;
  databases: Database[];
}

@Component({
  selector: 'app-horizontal-organization-chart-visualizer',
  templateUrl: './horizontal-organization-chart-visualizer.component.html',
  styleUrls: ['./horizontal-organization-chart-visualizer.component.css']
})
export class HorizontalOrganizationChartVisualizerComponent {}
<div class="controls">
  <label>System:
    <select [(ngModel)]="selectedSystemId" (change)="updateChart()">
      <option *ngFor="let s of data.systems" [value]="s.id">{{ s.id }}</option>
    </select>
  </label>

  <label>Database:
    <select [(ngModel)]="selectedDBId">
      <option *ngFor="let db of getDatabasesForSelectedSystem()" [value]="db.id">{{ db.id }}</option>
    </select>
  </label>

  <label>Field:
    <input [(ngModel)]="newFieldId" placeholder="Field name" />
  </label>
  <button (click)="addField()">Add Field</button>
</div>

<highcharts-chart 
  [Highcharts]="Highcharts"
  [options]="chartOptions"
  style="width: 100%; height: 600px; display: block;"
></highcharts-chart>
