import { Component, OnInit } from '@angular/core';
import * as Highcharts from 'highcharts';
import OrganizationModule from 'highcharts/modules/organization';
import Exporting from 'highcharts/modules/exporting';

// Initialize modules
OrganizationModule(Highcharts);
Exporting(Highcharts);

@Component({
  selector: 'app-horizontal-organization-chart-visualizer',
  templateUrl: './flow-visualizer.component.html',
  styleUrls: ['./flow-visualizer.component.css']
})
export class HorizontalOrganizationChartVisualizerComponent implements OnInit {
  Highcharts: typeof Highcharts = Highcharts;
  chartOptions: Highcharts.Options = {};

  ngOnInit(): void {
    this.chartOptions = {
      chart: {
        height: 600,
        inverted: true
      },
      title: {
        text: 'Horizontal Organization Chart'
      },
      series: [{
        type: 'organization',
        name: 'System Structure',
        keys: ['from', 'to'],
        data: [
          ['System A', 'DB1'],
          ['System A', 'DB2'],
          ['DB1', 'Field1'],
          ['DB1', 'Field2'],
          ['DB2', 'Field3'],
          ['System B', 'DB3'],
          ['System B', 'DB4'],
          ['DB3', 'FieldA'],
          ['DB4', 'FieldB']
        ] as [string, string][],
        nodes: [
          { id: 'System A', title: 'System A' },
          { id: 'DB1', title: 'DB1' },
          { id: 'DB2', title: 'DB2' },
          { id: 'Field1', title: 'Field1' },
          { id: 'Field2', title: 'Field2' },
          { id: 'Field3', title: 'Field3' },
          { id: 'System B', title: 'System B' },
          { id: 'DB3', title: 'DB3' },
          { id: 'DB4', title: 'DB4' },
          { id: 'FieldA', title: 'FieldA' },
          { id: 'FieldB', title: 'FieldB' }
        ],
        colorByPoint: false,
        color: '#007ad0',
        dataLabels: {
          color: 'white'
        },
        borderColor: 'white',
        nodeWidth: 65
      }]
    };
  }
}
